import { memo, useState, useCallback } from "react";
import { InputTextProps } from "./interface"

const InputText = ({ value, name, Label, onChange }: InputTextProps) => {

    const [change, setChange] = useState({
        name: "",
        value: ""
    })

    const handleChange = useCallback((event: { target: { value: string; }; }) => {
        setChange({ ...change, name: name, value: event.target.value })
        onChange(change)
    }, [change, name, onChange])

    return (
        <>
            <input type="text" value={value} placeholder={Label} onChange={handleChange} />
        </>
    )
}

export default memo(InputText)
