    {
        children,
        visible = false,
        label,
        onClose,
        onOK,
        submitTitle = "Sumbit",
        width = "50%",
        hideSaveButton = false,
        hideCloseButton = false
    }: ModalProp
) => {

    return (
        <>
            <ModalView isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalContent>
                    <ModalHeader>Modal Title</ModalHeader>
