import React, { memo, useState, useMemo, useCallback, useEffect } from 'react';
import "./style.css";
import Button from "../button/button";
import IconButton from '../button/iconButton';

interface TableProps {
    rowkey?: string | number;
    dataSource?: any[];
    columns: column[];
    title?: string;
    canEdit?: boolean;
    canAdd?: boolean;
    canDelete?: boolean;
    hideTools?: boolean;
    onAdd?: () => void;
    onEdit?: () => void | any;
    onDelete?: () => void;
    children?: any;
    Form?: any;
    actionColumn?: boolean;
    onAction?: () => void;
    actionLabel?: string;
    onSelectedRow?: any;
    canPrint?: boolean;
    onPrint?: () => void;
    addionalButtons?: any;
    onSave?: () => void;
    canSave?: boolean;
}

interface column {
    title: string,
    width?: number,
    dataIndex?: any,
}

const Table = ({
    dataSource,
    rowkey = "id",
    columns,
    hideTools = true,
    canEdit = false,
    canAdd = false,
    canDelete = false,
    onAdd,
    onEdit,
    onDelete,
    children,
    actionColumn = false,
    onAction,
    actionLabel = "",
    onSelectedRow,
    canPrint = false,
    onPrint,
    addionalButtons,
    onSave,
    canSave = false

}: TableProps) => {
    //@ts-ignore
    const [rowSelected, setRowSelected] = useState()

    const handleSelectedRow = useCallback((item: any) => () => {
        onSelectedRow(item)
        setRowSelected(item)
    }, [onSelectedRow])


    return (
        <>
            <div className="Table">
                <section>
                    <div className="head">
                        {children}
                    </div>
                    <div className="tools" hidden={hideTools}>
                        <div className="TableTools">
                            <IconButton
                                icon='fa-sharp fa-solid fa-plus'
                                onClick={onAdd}
                                disabled={!canAdd}
                            />
                            <IconButton
                                icon='fa-sharp fa-solid fa-pen-clip'
                                onClick={onEdit}
                                disabled={!canEdit}
                            />
                            <IconButton
                                icon='fa-sharp fa-solid fa-trash'
                                onClick={onDelete}
                                disabled={!canDelete}
                            />
                            <IconButton
                                icon='fa-solid fa-print'
                                onClick={onPrint}
                                disabled={!canPrint}
                            />
                            <IconButton
                                icon='fa-solid fa-floppy-disk'
                                onClick={onSave}
                                disabled={!canSave}
                            />
                            {addionalButtons}
                        </div>
                    </div>
                    <div className="tbl-header">
                        <table>
                            <thead>
                                <tr>
                                    {columns.map((item: any) => {
                                        return (
                                            <th style={{ width: `${item.width}%` }}>
                                                {item.title}
                                            </th>
                                        )
                                    })}
                                    <th hidden={!actionColumn}>Action</th>
                                </tr>
                            </thead>
                        </table>
                    </div>
                    <div className="tbl-content">
                        <table>
                            <tbody>
                                {dataSource?.map((item: any) => {
                                    return (
                                        <tr key={item[rowkey]} onClick={handleSelectedRow(item)}>
                                            {columns.map((column: any) => {
                                                return (
                                                    <td className={`${rowSelected === item && "selectedRow"}`} style={{ width: `${column.width}%` }}>
                                                        {item[column.dataIndex]}
                                                    </td>
                                                )
                                            })}
                                            <td hidden={!actionColumn}>
                                                <Button
                                                    label={actionLabel}
                                                    onClick={onAction}
                                                />
                                            </td>
                                        </tr>

                                    )
                                })}

                            </tbody>
                        </table>
                    </div>
                </section>
            </div >

            <div className="section">

                <table className="table table2">
                    <thead>
                        <tr className='table-secondary'>
                            {columns.map((item: any) => {
                                return (
                                    <th scope="col" style={{ width: `${item.width}%` }}>
                                        {item.title}
                                    </th>
                                )
                            })}
                            <th hidden={!actionColumn}>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/* <tr>
                            <td>1</td>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Jacob</td>
                            <td>Thornton</td>
                            <td>@fat</td>
                        </tr>
                        <tr>
                            <td>3</td>
                            <td >Larry the Bird</td>
                            <td>@twitter</td>
                        </tr> */}
                        {dataSource?.map((item: any) => {
                            return (
                                <tr key={item[rowkey]} onClick={handleSelectedRow(item)}>
                                    {columns.map((column: any) => {
                                        return (
                                            <td className={`${rowSelected === item && "selectedRow"} table-light`} style={{ width: `${column.width}%` }}>
                                                {item[column.dataIndex]}
                                            </td>
                                        )
                                    })}
                                    <td hidden={!actionColumn}>
                                        <Button
                                            label={actionLabel}
                                            onClick={onAction}
                                        />
                                    </td>
                                </tr>

                            )
                        })}
                    </tbody>
                </table>
            </div>
        </>
    )
}

export default Table;
