import { memo } from 'react';
import './style.css';
import { buttonProp } from "./interface";
import useTranslateLabel from '../../hooks/useTranslateLabel';

const Button = ({
    label = "",
    onClick,
    disabled,
    className,
    hidden,
    margin,
    width,
    height,
    noAuthorization = false
}: buttonProp) => {
    // const { tran_label } = useTranslateLabel({ noAuthorization: noAuthorization })
    return (
        <>
            <button className={`button btn btn-primary ${className} `} style={{
                width: `${width}`,
                margin: `${margin}`,
                height: `${height}`,
                display: "inline-block"
            }} onClick={onClick} disabled={disabled} hidden={hidden}></button>
        </>
    )
}

export default memo(Button)
