import { memo } from "react";
import Table from "./Table";
import useFetch from "../../hooks/useFetch";
import useTableControlsButtons from "./hooks/useTableControlsButtons"

interface ApiTableProps {
    api: string;
    postApi?: string;
    columns: {
        title: string,
        dataIndex: string,
        width: number
    }[];
    hideTools?: boolean;
    canAdd?: boolean;
    canEdit?: boolean;
    canDelete?: boolean;
    rowKey: string;
}

const ApiTable = ({
    api,
    postApi,
    columns,
    hideTools,
    canAdd,
    canEdit,
    canDelete,
    rowKey
}: ApiTableProps) => {

    const { data } = useFetch(api)
    const { setSelectedRow, onSaveAndInsertion, selectedRow } = useTableControlsButtons(postApi)

    return (
        <>

            <Table
                dataSource={data}
                columns={columns}
                hideTools={hideTools}
                canAdd={canAdd}
                canEdit={canEdit}
                canDelete={canDelete}
                rowkey={rowKey}
            // onAdd={handleAdd}
            // onEdit={handleEdit}
            // onDelete={() => {
            //     setSelectedRow({ ...selectedRow, query_status: "d" })
            //     onSaveAndInsertion()
            // }}
            // onSelectedRow={setSelectedRow}
            >
            </Table>

        </>
    )
}

export default memo(ApiTable)
