import { memo } from "react";
interface SelectProps {
    Option?: {
        label: string,
        value: number
    };
    Options: {
        label: string,
        value: number
    }[]
    onChange?: any;
    Label?: string;
    value?: number;
    name: string;
    width?: number | string;
    withLabel?: boolean;
    margin?: number | string;
    padding?: number | string
}



const Select = ({ width, Options, onChange, Label, value = 0, name, withLabel = false, margin, padding }: SelectProps) => {
    //@ts-ignore
    const handleValue = (event) => {
        Options.map((option) => {
            if (option.value === +event.target.value) {
                if (withLabel) {
                    onChange({ value: option.value, label: option.label, name: name })
                } else {
                    onChange({ value: option.value, name: name })
                }
            }
        })
    }

    return (
        <>
            <label htmlFor="exampleFormControlInput1" className="form-label">{Label}</label>
            <select className="form-select" style={{ width: width, padding: padding, margin: margin }} aria-label="Default select example" onChange={handleValue}>
                <option selected>{`Select`}</option>
                {Options.map((Option) => {
                    return (
                        <option key={Option.value} value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                    )
                })}
            </select>
            {/* <label htmlFor="select">{Label}</label>
            <select style={{ width: width }} name="cars" onChange={handleValue} >
                <option value="" hidden ></option>
                {Options.map((Option) => {
                    return (
                        <option key={Option.value} value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                    )
                })}
            </select> */}
        </>
    )
}

export default memo(Select)
