import { memo, useCallback, useEffect } from "react";
import SelectWithApi from "../../../components/Select/SelectWithApi";
import InputText from "../../../components/InputText/InputText";
import useFormManager from "../../../hooks/useFormManager"

interface InsertFormProp {
    activeItem: any;
    setActiveItem: any;
    itemState: any;
    itemChange: any;
}

const InsertForm = ({
    activeItem,
    setActiveItem,
    itemChange,
    itemState
}: InsertFormProp) => {

    const { item_id, width, height, size, quantity, price, total, notes, itemName } = itemState

    useEffect(() => {
        setActiveItem({ ...activeItem, size: +width * +height, total: +quantity * +price * +width * +height })
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [height, width, quantity, price])

    //@ts-ignore
    const handleItemChange = useCallback((onChange: any) => { itemChange(onChange); setActiveItem({ ...activeItem, itemName: onchange.label }) }, [])

    return (
        <>
            <SelectWithApi
                Api={"basicData/supplier_pop_data"}
                // onChange={onChange}
                // value={itemsType.supplier_id}
                Label="Supplier"
                width="10%"
            />
            <SelectWithApi
                Api={"basicData/items_pop_data"}
                onChange={itemChange}
                value={item_id}
                Label="Item"
                name="item_id"
                width="10%"
            />
            <InputText
                name="width"
                value={width}
                Label="width"
                onChange={itemChange}

            />
            <InputText
                name="height"
                value={height}
                Label="size"
                onChange={itemChange}
            />
            <InputText
                name="size"
                value={size}
                Label="size"
                onChange={itemChange}
                disabled
            />
            <InputText
                name="quantity"
                value={quantity}
                Label="quantity"
                onChange={itemChange}
            />
            <InputText
                name="price"
                value={price}
                Label="price"
                onChange={itemChange}
            />
            <InputText
                name="total"
                value={total}
                Label="total"
                onChange={itemChange}
                disabled
            />
            <InputText
                name="notes"
                value={notes}
                Label="notes"
                onChange={itemChange}
            />
        </>
    )
}

export default memo(InsertForm)
