import { memo, useState, useCallback } from "react";
import Table from "./Table";
import useFetch from "../../hooks/useFetch";
import useTableControlsButtons from "./hooks/useTableControlsButtons"

interface ApiTableProps {
    api: string;
    postApi?: string;
    columns: {
        title: string,
        dataIndex: string,
        width: number
    }[];
    hideTools?: boolean;
    canAdd?: boolean;
    canEdit?: boolean;
    canDelete?: boolean;
    rowKey: string;
    modalForm?: any
}

const ApiTable = ({
    api,
    postApi,
    columns,
    hideTools,
    canAdd,
    canEdit,
    canDelete,
    rowKey,
    modalForm
}: ApiTableProps) => {

    console.log(modalForm.props)

    const { data } = useFetch(api)
    const { setSelectedRow, onSaveAndInsertion } = useTableControlsButtons(postApi)
    const [rows, setRows] = useState({})
    const [modal, setModal] = useState(false)
    //@ts-ignore
    modalForm.props.visable = modal
    const handleAdd = useCallback(() => {
        setSelectedRow({ query_status: "n" })
    }, [setSelectedRow])
    const handleEdit = useCallback(() => {
        setSelectedRow({ ...rows, query_status: "u" })
    }, [rows, setSelectedRow])
    const handleDelete = () => {
        setSelectedRow({ ...rows, query_status: "d" })
        onSaveAndInsertion()
    }

    return (
        <>
            {modalForm}
            <Table
                dataSource={data}
                columns={columns}
                hideTools={hideTools}
                canAdd={canAdd}
                canEdit={canEdit}
                canDelete={canDelete}
                rowkey={rowKey}
                onAdd={handleAdd}
                onEdit={handleEdit}
                onDelete={handleDelete}
                onSelectedRow={setRows}
            >
            </Table>

        </>
    )
}

export default memo(ApiTable)
