import { memo, useCallback } from 'react';
import Modal from "../../../components/Modal/Modal";
import { ModalViewProp } from "../../../Types/general";
import useFormManager from '../../../hooks/useFormManager';
import InputText from '../../../components/InputText/InputText';

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {

    const { onChange, state } = useFormManager(selectedRow, setSelectedRow)

    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <InputText
                name='name'
                //@ts-ignore
                value={state.name}
                onChange={onChange}
                Label='Employee Name'
                width="100%"
            />
        </Modal>
    )
};

export default memo(ModalView)
