import { memo } from "react";
import Select from "./Select";
import useFetch from "../../hooks/useFetch";

interface SelectWithApiProps {
    Api: string;
    setValue?: any;
    returnLabel?: boolean;
    value?: number
}

const SelectWithApi = ({ Api, setValue, returnLabel, value = 0 }: SelectWithApiProps) => {

    const { data } = useFetch(Api)
    const setVlaue2 = () => {
        if (returnLabel) {
            data.map((option: any) => {
                if (option.value === +value) {
                    setValue(option)
                }
            })
        } else {
            console.log(value)
            setValue(+value)
        }
    }

    return (
        <>
            <Select
                Options={data}
                setValue={setVlaue2}
                // returnLabel={returnLabel}
                value={value}
            />
        </>
    )
}

export default memo(SelectWithApi);
