import { useEffect, useState, useCallback } from "react";
import useFetch from "./useFetch";

interface useTranslateLabelProps{
    label: string;
    chunkLabels?: []
}

const useTranslateLabel = ({label}: useTranslateLabelProps)=>{

    const [fetchOptions, setFetchOptions] = useState({
        link: "QUERY_LABELS",
        params: {
            label: label,
            p_language: 2,
            chunk_labels: []
        }
    })

const labels = useCallback((e: any)=>{
    setFetchOptions({
        link: ""
    })
},[])

const { data, runFetch}  = useFetch({
    link: fetchOptions.link,
    params:fetchOptions.params,
    fetchOnFirstRun: false
})

useEffect(()=>{
    if(label){
        runFetch()
    }
// eslint-disable-next-line react-hooks/exhaustive-deps
},[label])

return {tran_label: data[label], chunkLabels: data[0]}

}

export default useTranslateLabel
