import { memo, useState } from 'react';

interface itemsType {
    itemCode?: number;
    item?: string;
    width?: number;
    height?: number;
    size?: number;
    quantity?: number;
    price?: number;
    total?: number;
    notes?: string;
}

const SupplierInvoice = () => {

    const [items, setItems] = useState<itemsType[]>({
        itemCode: 0,
        item: '',
        width: 0,

    })

    return (
        <>
            <div className="SupplierInvoice">
                <div className="Table">
                    <section>
                        <div className="tbl-header">
                            <table>
                                <thead>
                                    <tr>
                                        <th>Item</th>
                                        <th>Width</th>
                                        <th>Heigh</th>
                                        <th>Size</th>
                                        <th>Quantity</th>
                                        <th>Price</th>
                                        <th>Total</th>
                                        <th>Notes</th>
                                        <th></th>
                                    </tr>
                                </thead>
                            </table>
                        </div>
                        <div className="tbl-content">
                            <table>
                                <tbody>
                                    {items.map((item) => {
                                        return (<tr>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td></td>
                                            <td><button>delete</button></td>
                                        </tr>)
                                    })}
                                    <button>add</button>
                                </tbody>
                            </table>
                        </div>
                    </section>
                </div >
            </div>
        </>
    )
}

export default memo(SupplierInvoice);
