import { memo } from "react";
interface SelectProps {
    Option?: {
        label: string,
        value: number
    };
    Options: {
        label: string,
        value: number
    }[]
    onChange?: any;
    Label?: string;
    value?: number;
    name?: string;
    width?: number | string;
}

const Select = ({ width, Options, onChange, Label, value = 0, name }: SelectProps) => {
    //@ts-ignore
    const handleValue = (event) => {
        Options.map((option) => {
            if (option.value === +event.target.value) {
                onChange({ value: option.value, label: option.label, name: name })
            }
        })
    }
    return (
        <>
            <div style={{ width: width }}>
                <label htmlFor="select">{Label}</label>
                <select style={{ width: width }} name="cars" onChange={handleValue}>
                    <option value="" hidden ></option>
                    {Options.map((Option) => {
                        return (
                            <option key={Option.value} value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                        )
                    })}
                </select>
            </div>
        </>
    )
}

export default memo(Select)
