import { memo } from "react";
import Select from "./Select";
import useFetch from "../../hooks/useFetch";
import { options } from "../../Pages/SupplierInvoice/constants";

interface SelectWithApiProps {
    Api: string;
    setValue?: any;
    setLabel?: any;
    value?: number
}

const SelectWithApi = ({ Api, setValue, setLabel, value = 0 }: SelectWithApiProps) => {

    const { data } = useFetch(Api)
    if (setLabel) {
        data.map((oprion: any) => {
            if (oprion.value === +value) {
                setLabel()
            }
        })
    }

    return (
        <>
            <Select
                Options={data}
                setValue={setValue}
                setLabel={setLabel}
                value={value}
            />
        </>
    )
}

export default memo(SelectWithApi);
