import { memo } from "react";
import Table from "./Table";
import useFetch from "../../hooks/useFetch";

interface ApiTableProps {
    api: string;
    postApi?: string;
    columns: {
        title: string,
        dataIndex: string,
        width: number
    }[];
    hideTools?: boolean;
    canAdd?: boolean;
    canEdit?: boolean;
    canDelete?: boolean;
}

const ApiTable = ({
    api,
    postApi,
    columns,
    hideTools,
    canAdd,
    canEdit,
    canDelete
}: ApiTableProps) => {

    const { data } = useFetch(api)

    return (
        <>

            <Table
                dataSource={data}
                columns={columns}
                hideTools={hideTools}
                canAdd={canAdd}
                canEdit={canEdit}
                canDelete={canDelete}
            // onAdd={handleAdd}
            // onEdit={handleEdit}
            // onDelete={() => {
            //     setSelectedRow({ ...selectedRow, query_status: "d" })
            //     onSaveAndInsertion()
            // }}
            // rowkey={"customer_id"}
            // onSelectedRow={setSelectedRow}
            >
            </Table>

        </>
    )
}

export default memo(ApiTable)
