import {useState, useCallback, useEffect} from "react";

const useFormManager = (initialValue?: any, setSelectedRow?: any)=>{
    const [initialValues, setInitialValues] = useState()
    const [state, setState] = useState({})
    const [v, setv] = useState()

    const onChange = useCallback((change: any)=>{
        //@ts-ignore
            setSelectedRow({...initialValue, [change.name]: change.value})
            setState({...state, [change.name]: change.value})
            setInitialValues(initialValues)
    },[initialValues, initialValue, setSelectedRow, state])

    useEffect(()=>{
        setState(initialValue);
    },[initialValue])

    const resetState = useCallback(()=>{
        setSelectedRow(initialValues)
    },[initialValues, setSelectedRow])


    const additionalChange = useCallback((additionalChanges: any)=>{
        // setv(additionalChanges)
        console.log(additionalChanges)
    },[])


    return{state, onChange, resetState, additionalChange}
}

export default useFormManager
