import React,{ useState, useEffect, useCallback } from "react";

//@ts-ignore
const usePost = (apiPost) => {
    const url = `http://144.24.209.19:9090/api/v1/${apiPost}`
    const [success, setSuccess] = useState(false)
    const [row, setRow] = useState<{}>()
    // const postData = useCallback(async (dataToPost: any, link: string) => {
    // await fetch(link, {
    //         method: "POST",
    //         headers: { "Content-type": "application/json; charset=UTF-8" },
    //         body: JSON.stringify(dataToPost)
    //     }).then(function(response) {
    //         console.log(response.body)
    //     })
    // }, [])

    const postData = async (dataToPost: any, link: string) => {
    const location = window.location.hostname;
    const settings = {
        method: 'POST',
        headers: {
            Accept: 'application/json',
            'Content-Type': 'application/json',
        }
    };
    try {
        const fetchResponse = await fetch(`http://${location}:9000/api/sensors/`, settings);
        const data = await fetchResponse.json();
        return data;
    } catch (e) {
        return e;
    }    

}    

useEffect(()=>{
    if(apiPost && row){
    postData(row, url)
    }
}, [row, postData, url, success, apiPost])

      return{success, setRow}
}

export default usePost
