import { memo } from "react";
import SelectWithApi from "../../../components/Select/SelectWithApi";
import InputText from "../../../components/InputText/InputText";

interface InsertFormProp {
    activeItem: any;
    setActiveItem: any
}

const InsertForm = ({
    activeItem,
    setActiveItem
}: InsertFormProp) => {

    const { state: active, onChange, resetState } = useFormManager(activeItem, setActiveItem)
    //@ts-ignore
    const { item_id, width, height, size, quantity, price, total, notes, itemName } = active
    return (
        <>
            <SelectWithApi
                Api={"basicData/supplier_pop_data"}
                onChange={onChange}
                // value={itemsType.supplier_id}
                Label="Supplier"
                width="10%"
            />
            <SelectWithApi
                Api={"basicData/items_pop_data"}
                onChange={onChange}
                // value={activeItem.item_id}
                Label="Item"
                name="item_id"
                width="10%"
            />
            <InputText
                name="height"
                // value={activeItem.width}
                Label="height"
                onChange={onChange}
            />
            <InputText
                name="size"
                // value={activeItem.width}
                Label="size"
                onChange={onChange}
            />
            <InputText
                name="quantity"
                // value={activeItem.width}
                Label="Width"
                onChange={onChange}
            />
            <InputText
                name="width"
                // value={activeItem.width}
                Label="Width"
                onChange={onChange}
            />
            <InputText
                name="width"
                // value={activeItem.width}
                Label="Width"
                onChange={onChange}
            />
            <InputText
                name="width"
                // value={activeItem.width}
                Label="Width"
                onChange={onChange}
            />
            <InputText
                name="width"
                // value={activeItem.width}
                Label="Width"
                onChange={onChange}
            />
        </>
    )
}

export default memo(InsertForm)
