import { memo, useCallback } from "react";
import { InputTextProps } from "./interface"
import "./Style.css"

const InputText = ({ disabled, value, name, Label, onChange, width, type = "text", placeHolder }: InputTextProps) => {

    const handleChange = useCallback((event: { target: { value: string; }; }) => {
        onChange({ name: name, value: event.target.value })
    }, [name, onChange])

    return (
        <>
            <div>
                {/* <div className="input">
                <input disabled={disabled} type="text" value={value} placeholder={Label} onChange={handleChange} />
            </div> */}
                {/* <div className="mb-1" style={{ width: width }}>
                    <label htmlFor="exampleFormControlInput1" className="form-label">{Label}</label>
                    <input type={type} className="form-control" id="exampleFormControlInput1" placeholder={placeHolder} disabled={disabled} onChange={handleChange} value={value} />
                </div> */}
                {/* is-invalid */}
                <form className="form-floating" style={{ width: width, }}>
                    <input type={type} className="form-control " id="floatingInputInvalid" placeholder={placeHolder} value={value} onChange={onChange} style={{ height: "60px" }} />
                    <label htmlFor="floatingInputInvalid">{Label}</label>
                </form>
            </div>
        </>
    )
}

export default memo(InputText)
