import { memo } from 'react';

const SupplierInvoice = () => {
    return (
        <>
            <div className="SupplierInvoice">
                <div className="Table">
                    <section>
                        <div className="tbl-header">
                            <table>
                                <thead>
                                    <tr>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                            </table>
                        </div>
                        <div className="tbl-content">
                            <table>
                                <tbody>
                                    {dataSource?.map((item: any) => {
                                        return (
                                            <tr key={item[rowkey]} onClick={handleSelectedRow(item)}>
                                                {columns.map((column: any) => {
                                                    return (
                                                        <td className={`${rowSelected === item && "selectedRow"}`} style={{ width: `${column.width}%` }}>
                                                            {item[column.dataIndex]}
                                                        </td>
                                                    )
                                                })}
                                                <td hidden={!actionColumn}>
                                                    <Button
                                                        label={actionLabel}
                                                        onClick={onAction}
                                                    />
                                                </td>
                                            </tr>
                                        )
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </section>
                </div >
            </div>
        </>
    )
}

export default memo(SupplierInvoice);
