import Button from "../button/button";
import IconButton from '../button/iconButton';

interface TableProps {
    rowkey?: string | number;
    dataSource?: any[];
    columns: column[];
    title?: string;
    canEdit?: boolean;
    canAdd?: boolean;
    canDelete?: boolean;
    hideTools?: boolean;
    onAdd?: () => void;
    onEdit?: () => void | any;
    onDelete?: () => void;
    children?: any;
    Form?: any;
    actionColumn?: boolean;
    onAction?: () => void;
    actionLabel?: string;
    onSelectedRow?: any;
    canPrint?: boolean;
    onPrint?: () => void;
    addionalButtons?: any;
    onSave?: () => void;
    canSave?: boolean;
}

