import { memo, useState, useCallback } from 'react';
import "./style.css";
import SelectWithApi from "../../components/Select/SelectWithApi";
import Button from "../../components/button/button";
import PrintModal from "../../components/Modal/PrintModal";
import DatePicker from "../../components/datePicker/DatePicker"
import usePost from '../../hooks/usePost';


interface itemsType {
    item_id: number;
    width: number;
    height: number;
    size: number;
    quantity: number;
    price: number;
    total: number;
    notes?: string;
    itemName: string
}
interface invoiceDtls {
    supplier_id: number;
    date: string;
    items: itemsType[];
    query_status: string;
}

const SupplierInvoice = () => {

    const { setRow } = usePost("invoices/supplier_invoice_dml")

    const [itemsType, setItemsType] = useState<invoiceDtls>({
        supplier_id: 1,
        date: "2022-8-2",
        items: [],
        query_status: "n"
    })
    const [activeItem, setActiveItem] = useState<itemsType>({
        item_id: 0,
        width: 0,
        height: 0,
        size: 0,
        quantity: 0,
        price: 0,
        total: 0,
        notes: "",
        itemName: ""
    })

    const resetItemsType = useCallback(() => {
        setItemsType({
            supplier_id: 1,
            date: "2022-8-2",
            items: [],
            query_status: "n"
        })
    }, [])

    const handleAdd = useCallback(() => {
        setItemsType({ ...itemsType, items: [...itemsType.items, activeItem] })
        setActiveItem({
            item_id: 0,
            width: 0,
            height: 0,
            size: 0,
            quantity: 0,
            price: 0,
            total: 0,
            notes: "",
            itemName: ""
        })
    }, [activeItem, itemsType]);
    const handleDelete = useCallback(() => {
        console.log(typeof 0.2)
    }, []);
    const handleSave = useCallback(() => {
        setRow(itemsType)
        resetItemsType()
    }, [itemsType, resetItemsType, setRow]);
    const handleClear = useCallback(() => {
        resetItemsType()
    }, [resetItemsType]);
    const handlePrint = useCallback(() => {

    }, [])

    //@ts-ignore
    const handleWidth = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, width: +event.target.value, size: activeItem.height * +event.target.value }) }, [activeItem])
    const handleHeight = useCallback((event: { target: { value: string | number; }; }) => {
        setActiveItem({ ...activeItem, height: +event.target.value, size: +event.target.value * activeItem.width })
    }, [activeItem])
    const handleQuantity = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, quantity: +event.target.value }) }, [activeItem])
    const handlePrice = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, price: +event.target.value }) }, [activeItem])
    const handleNotes = useCallback((event: { target: { value: string; }; }) => { setActiveItem({ ...activeItem, notes: event.target.value }) }, [activeItem])

    const handleItemChange = useCallback((value: { value: number, label: string }) => {
        setActiveItem({ ...activeItem, item_id: +value.value, itemName: value.label })
    }, [activeItem])
    const handleSupplierChange = useCallback((value: string) => {
        setItemsType({ ...itemsType, supplier_id: +value })
    }, [itemsType])
    const handleDateChange = useCallback((value: string) => {
        setItemsType({ ...itemsType, date: value })
    }, [itemsType])

    console.log(typeof 0.2)

    return (
        <>
            <div className="SupplierInvoice">
                <SelectWithApi
                    Api={"basicData/supplier_pop_data"}
                    setValue={handleSupplierChange}
                    value={itemsType.supplier_id}
                />
                <DatePicker
                    value={itemsType.date}
                    setValue={handleDateChange}
                />
                <div className="Table">
                    <section>
                        <div className="tools">
                            <div className="TableTools">
                                <button onClick={handleAdd} >
                                    <i className="fa-sharp fa-solid fa-plus"></i>
                                </button>
                                <button onClick={handleSave}>
                                    <i className="fa-solid fa-floppy-disk"></i>
                                </button>
                                <button onClick={handleClear} >
                                    <i className="fa-sharp fa-solid fa-broom"></i>
                                </button>
                                <button onClick={handlePrint}>
                                    <i className="fa-solid fa-print"></i>
                                </button>
                            </div>
                        </div>
                        <div className="tbl-header">
                            <table>
                                <thead>
                                    <tr>
                                        <th className='item'>Item</th>
                                        <th>Width</th>
                                        <th>Heigh</th>
                                        <th>Size</th>
                                        <th>Quantity</th>
                                        <th>Price</th>
                                        <th>Total</th>
                                        <th>Notes</th>
                                        <th></th>
                                    </tr>
                                </thead>
                            </table>
                        </div>
                        <div className="tbl-content">
                            <table>
                                <tbody>
                                    {itemsType.items.map((item) => {
                                        return (<tr key={item.item_id}>
                                            <td className='item'>{item.itemName}</td>
                                            <td>{item.width}</td>
                                            <td>{item.height}</td>
                                            <td>{item.size}</td>
                                            <td>{item.quantity}</td>
                                            <td>{item.price}</td>
                                            <td>{item.total}</td>
                                            <td>{item.notes}</td>
                                            <td><Button
                                                label={"Delete"}
                                                onClick={handleDelete}
                                            /></td>
                                        </tr>)
                                    })}
                                    <tr>
                                        <td className='item'><SelectWithApi
                                            Api={"basicData/items_pop_data"}
                                            setValue={handleItemChange}
                                            value={activeItem.item_id}
                                            Label={true}
                                        /></td>
                                        <td><input type="text" onChange={handleWidth} value={activeItem.width} /></td>
                                        <td><input type="text" onChange={handleHeight} value={activeItem.height} /></td>
                                        <td><input type="text" disabled value={activeItem.size} /></td>
                                        <td><input type="text" onChange={handleQuantity} value={activeItem.quantity} /></td>
                                        <td><input type="text" onChange={handlePrice} value={activeItem.price} /></td>
                                        <td><input type="text" value={activeItem.total} disabled /></td>
                                        <td><input type="text" onChange={handleNotes} value={activeItem.notes} /></td>
                                        <td><Button
                                            label={"Delete"}
                                            disabled
                                        /></td>
                                    </tr>
                                </tbody>
                            </table>
                        </div>
                    </section>
                </div >
            </div>
        </>
    )
}

export default memo(SupplierInvoice);
