import React, { memo, useCallback } from 'react';
import Modal from "../../../components/Modal/Modal";

interface ModalViewProp {
    visible: boolean;
    onOK: () => void;
    onClose: () => void;
    selectedRow: any;
    setSelectedRow: any
}

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {

    const onChangeName = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, item_name: event.target.value })
    }, [selectedRow, setSelectedRow]);
    const onChangeEmail = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, unit: event.target.value })
    }, [selectedRow, setSelectedRow])
    const onChangePhone = useCallback((event?: any) => {
        setSelectedRow({ ...selectedRow, base_price: event.target.value })
    }, [selectedRow, setSelectedRow])



    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <label htmlFor="">Item Name</label>
            <input type="text" value={selectedRow.item_name} onChange={onChangeName} />
            <label htmlFor="">Unit</label>
            <input type="text" value={selectedRow.unit} onChange={onChangeEmail} />
            <label htmlFor="">Base Price</label>
            <input type="text" value={selectedRow.base_price} onChange={onChangePhone} />
        </Modal>
    )
};

export default memo(ModalView)
