import { memo } from 'react';
import Modal from "../../../components/Modal/Modal";
import InputText from '../../../components/InputText/InputText';
import useFormManager from '../../../hooks/useFormManager';
import { ModalViewProp } from "../../../Types/general"
import { Flex, Square } from '@chakra-ui/react';

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {
    const { state, onChange } = useFormManager({ initialValue: selectedRow, setSelectedRow: setSelectedRow })
    //@ts-ignore
    const { name, email, phone, mobile, address } = state

    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <Flex>
                <InputText
                    name="name"
                    Label='Name'
                    onChange={onChange}
                    value={name}
                    width="30%"
                />
                <InputText
                    name="email"
                    Label='Email'
                    onChange={onChange}
                    value={email}
                />
            </Flex>
            <InputText
                name="phone"
                Label='Phone'
                onChange={onChange}
                value={phone}
            />
            <InputText
                name="mobile"
                Label='Mobile'
                onChange={onChange}
                value={mobile}
            />

            <InputText
                name="address"
                Label='Address'
                onChange={onChange}
                value={address}
            />
        </Modal>
    )
};

export default memo(ModalView)
