import { memo } from "react";
import SelectWithApi from "../../../components/Select/SelectWithApi";
import InputText from "../../../components/InputText/InputText";
import useFormManager from "../../../hooks/useFormManager"

interface InsertFormProp {
    activeItem: any;
    setActiveItem: any
}

const InsertForm = ({
    activeItem,
    setActiveItem
}: InsertFormProp) => {

    const { state, onChange: itemChange, resetState } = useFormManager(activeItem, setActiveItem)
    //@ts-ignore
    const { item_id, width, height, size, quantity, price, total, notes, itemName } = state
    return (
        <>
            <SelectWithApi
                Api={"basicData/supplier_pop_data"}
                // onChange={onChange}
                // value={itemsType.supplier_id}
                Label="Supplier"
                width="10%"
            />
            <SelectWithApi
                Api={"basicData/items_pop_data"}
                onChange={itemChange}
                value={item_id}
                Label="Item"
                name="item_id"
                width="10%"
            />
            <InputText
                name="width"
                value={width}
                Label="width"
                onChange={itemChange}
            />
            <InputText
                name="height"
                value={height}
                Label="size"
                onChange={itemChange}
            />
            <InputText
                name="size"
                value={width}
                Label="size"
                onChange={itemChange}
            />
            <InputText
                name="quantity"
                value={width}
                Label="Width"
                onChange={itemChange}
            />
            <InputText
                name="width"
                value={width}
                Label="Width"
                onChange={itemChange}
            />
            <InputText
                name="width"
                value={width}
                Label="Width"
                onChange={itemChange}
            />
            <InputText
                name="width"
                value={width}
                Label="Width"
                onChange={itemChange}
            />
            <InputText
                name="width"
                value={width}
                Label="Width"
                onChange={itemChange}
            />
        </>
    )
}

export default memo(InsertForm)
