import { memo, useCallback, useState } from "react";
import ApiTable from "../../components/TableView/ApiTable";
import { columns } from "./constant";
import FormView from "./Partials/FormView";
import useFormManager from "../../hooks/useFormManager";
import DetailView from "./Partials/DetailView";

const InvoicesSearch = () => {

    const searchParamsBaseValues = {
        invoice_type: "",
        number: "",
        name: "",
        date_from: "",
        date_to: ""
    }
    const [row, setRow] = useState({
        number: ""
    })
    const { state: searchParams, onChange: changeSearchParams } = useFormManager(searchParamsBaseValues)
    //@ts-ignore
    const { invoice_type, number, name, date_from, date_to } = searchParams

    const onSelectedRow = useCallback((row?: any) => {
        setRow(row)
    }, [])

    return (
        <>
            <FormView
                changeSearchParams={changeSearchParams}
                searchParams={searchParams}
            />
            <div style={{ display: "flex" }}>
                <ApiTable
                    columns={columns}
                    //@ts-ignore
                    api="invoices/get_invoice_master"
                    rowKey="invoice_id"
                    width="35%"
                    fetchOnFirstRun
                    params={{
                        invoice_type: invoice_type,
                        number: number,
                        name: name,
                        date_from: date_from,
                        date_to: date_to
                    }}
                    onSelectedRow={onSelectedRow}
                />
                <div style={{ width: "65%" }}>
                    {/* <DetailView /> */}
                    <ApiTable
                        columns={columns}
                        api="invoices/get_invoice_detail"
                        rowKey=""
                        fetchOnFirstRun
                        params={{
                            invoice_type: invoice_type,
                            number: row.number
                        }}
                    />
                </div>
            </div>
        </>
    )
}

export default memo(InvoicesSearch)
