import { memo } from "react";

interface iconButtonProp {
    icon: string;
    onClick?: () => void;
    disabled?: boolean;
    width?: number | string;
    height?: number | string;
    color?: string
}

const IconButton = ({
    icon,
    onClick,
    disabled,
    width = "20px",
    height,
    color
}: iconButtonProp) => {
    return (
        <>
            <button style={{
                border: "none",
                width: width,
                height: height,
                backgroundColor: color,
            }} disabled={!disabled} onClick={onClick} >
                <i className={icon}></i>
            </button>
        </>
    )
}

export default memo(IconButton)
