import { memo, useCallback } from 'react';
import Modal from "../../../components/Modal/Modal";
import { ModalViewProp } from "../../../Types/general";
import useFormManager from '../../../hooks/useFormManager';
import InputText from '../../../components/InputText/InputText';

interface dd {
    name: string;
}

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {


    const { onChange, state } = useFormManager({
        initialValue: selectedRow,
        setSelectedRow: setSelectedRow
    })

    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <InputText
                name='name'
                value={state.name}
                onChange={onChange}
                Label='Employee Name'
                width="93%"
            />
            <InputText
                name='date_of_hiring'
                value={state.date_of_hiring}
                onChange={onChange}
                Label='Date of Hiring'
                width="35%"
                type='date'
            />
            <InputText
                name='address'
                value={state.address}
                onChange={onChange}
                Label='Address'
                width="55%"
            />
            <InputText
                name='name'
                value={state.name}
                onChange={onChange}
                Label='Employee Name'
                width="20%"
