                width="20%"
            />
            <InputText
                name='name'

                value={state.name}
                onChange={onChange}
                Label='Employee Name'
                width="20%"
            />
            <InputText
                name='name'

                value={state.name}
                onChange={onChange}
                Label='Employee Name'
                width="20%"
            />
        </Modal>
    )
};

export default memo(ModalView)
