import { useToast } from '@chakra-ui/react'

interface notificationProp {
    label: string;
    body: string;
    type: "success" | "error" | "warning" | "info"
}
export default function Notification({label = "", body = "", type = "warning"}:notificationProp){
    const toast = useToast()
    toast({
            position: "top-right",
            title: `${label}`,
            description: `${body}`,
            status: `${type}`,
            duration: 9000,
            isClosable: true,
        })
}
