import React, { memo, useCallback, useEffect, useState } from 'react';
import Header from '../../components/Header/component';
import Footer from '../../components/Footer/component';
import useCheckUser from '../../hooks/useCheckUser';
import './style.css';
import useFetch from '../../hooks/useFetch';
import ModalView from "./Partials/ModalView";
// import FormView from "./Partials/FormView";
import Table from "../../components/TableView/Table";
import useTableControlsButtons from "../../components/TableView/hooks/useTableControlsButtons";
import ApiTable from "../../components/TableView/ApiTable";
import { columns } from "./constants";

interface SupplierType {
    id?: number;
    name: string;
    email?: string;
    phone?: string;
    mobile?: string;
    address?: string
}

const Suppliers = () => {
    return (
        <>
            <Header />
            <div className="Suppliers" hidden={hidden}>
                <ApiTable
                    api={"basicData/suppliers"}
                    postApi={"basicData/customers_dml"}
                    columns={columns}
                    hideTools={false}
                    canEdit={true}
                    canAdd={true}
                    canDelete={true}
                    rowKey={"supplier_id"}
                    Modal={ModalView}
                />
            </div>
            <Footer />
        </>
    )
};

export default memo(Suppliers);
