import { memo, useCallback } from 'react';
import Modal from "../../../components/Modal/Modal";
import InputText from '../../../components/InputText/InputText';
import useFormManager from '../../../hooks/useFormManager';

interface ModalViewProp {
    visible: boolean;
    onOK: () => void;
    onClose: () => void;
    selectedRow: any;
    setSelectedRow: any
}

const ModalView = ({
    visible,
    onOK,
    onClose,
    setSelectedRow,
    selectedRow
}: ModalViewProp) => {
    console.log(selectedRow)

    const { state, onChange } = useFormManager(selectedRow, setSelectedRow)
    //@ts-ignore
    const { name, email, phone, mobile, address } = state

    return (
        <Modal
            visible={visible}
            label={"Details"}
            onOK={onOK}
            onClose={onClose}
        >
            <InputText
                name="name"
                Label='Name'
                onChange={onChange}
                value={name}
            />
            <InputText
                name="email"
                Label='Email'
                onChange={onChange}
                value={email}
            />
            <InputText
                name="phone"
                Label='Phone'
                onChange={onChange}
                value={phone}
            />
            <InputText
                name="mobile"
                Label='Mobile'
                onChange={onChange}
                value={mobile}
            />
            <InputText
                name="address"
                Label='Address'
                onChange={onChange}
                value={address}
            />
        </Modal>
    )
};

export default memo(ModalView)
