import { useEffect, useState, useCallback } from "react";
import useFetch from "./useFetch";

interface useTranslateLabelProps{
    label?: string;
    chunkLabel?: string[]
}

interface FetchOptionsType{
    link: string;
    params:{
        label?: string;
        p_language: 1 | 2,
        chunk_labels?: string[]
    }
}

const useTranslateLabel = ({label = "", chunkLabel = []}: useTranslateLabelProps)=>{

    const [fetchOptions, setFetchOptions] = useState<FetchOptionsType>({
        link: "QUERY_LABELS",
        params: {
            label: label,
            p_language: 2,
            chunk_labels: []
        }
    })

    const [chunkLabels, setChunkLabels] = useState()

const { data, runFetch}  = useFetch({
    link: fetchOptions.link,
    params:fetchOptions.params,
    fetchOnFirstRun: false
})

const labels = useCallback(()=>{
    setFetchOptions({
        link: "QUERY_CHUNK_LABELS",
        params:{
            p_language: 2,
            chunk_labels: chunkLabel
        }
    })
     runFetch()
     setChunkLabels(data)
},[chunkLabel, data, runFetch])


useEffect(()=>{
    if(label !== ""){
        runFetch()
    }else if (chunkLabel.length !== 0){
        setFetchOptions({
        link: "QUERY_CHUNK_LABELS",
        params:{
            p_language: 2,
            chunk_labels: chunkLabel
        }
    })
     runFetch()
    }
// eslint-disable-next-line react-hooks/exhaustive-deps
},[label])

return {tran_label: data[label], chunkLabels, labels}

}

export default useTranslateLabel
