import { useCallback, useState } from "react";
import useFetch from "./useFetch";

interface useTranslateLabelProps{
    label?: string;
    chunkLabel?: string[];
    noAuthorization?: boolean;
}

const useTranslateLabel = ({chunkLabel, noAuthorization = false}: useTranslateLabelProps)=>{

const [label, setLabel] = useState<string>()

const { data, runFetch}  = useFetch({
    link: "QUERY_LABELS",
    params: {
            label: label,
            p_language: 2,
            // chunk_labels: chunkLabel
        },
        noAuthorization: noAuthorization
})         


// useEffect(()=>{
//     if(label !== "" && label !== undefined){
//         runFetch()
//     }
// // eslint-disable-next-line react-hooks/exhaustive-deps
// },[label])

const tran_label = useCallback((dd: string)=>{
    if (dd){
        setLabel(dd)
    runFetch()
    if(data.label){
        return(data.label)
    }else {
        return
    }
}
},[])

return {tran_label}

}

export default useTranslateLabel
