import { memo, useState, useCallback } from "react";
import Table from "./Table";
import useFetch from "../../hooks/useFetch";
import useTableControlsButtons from "./hooks/useTableControlsButtons"

interface ApiTableProps {
    api: string;
    postApi?: string;
    columns: {
        title: string,
        dataIndex: string,
        width: number
    }[];
    hideTools?: boolean;
    canAdd?: boolean;
    canEdit?: boolean;
    canDelete?: boolean;
    rowKey: string;
}

const ApiTable = ({
    api,
    postApi,
    columns,
    hideTools,
    canAdd,
    canEdit,
    canDelete,
    rowKey
}: ApiTableProps) => {

    const { data } = useFetch(api)
    const { setSelectedRow, onSaveAndInsertion, selectedRow } = useTableControlsButtons(postApi)
    const [row, setRow] = useState({})

    const handleAdd = useCallback(() => {
        setRow({ query_status: "n" })
    }, [])
    const handleEdit = useCallback(() => {
        setRow({ ...row, query_status: "u" })
    }, [row])
    const handleDelete = useCallback(() => {
        setRow({ query_status: "d" })
    }, [])

    console.log(row)

    return (
        <>

            <Table
                dataSource={data}
                columns={columns}
                hideTools={hideTools}
                canAdd={canAdd}
                canEdit={canEdit}
                canDelete={canDelete}
                rowkey={rowKey}
                onAdd={handleAdd}
                onEdit={handleEdit}
                // onDelete={() => {
                //     setSelectedRow({ ...selectedRow, query_status: "d" })
                //     onSaveAndInsertion()
                // }}
                onSelectedRow={setRow}
            >
            </Table>

        </>
    )
}

export default memo(ApiTable)
