    visible?: boolean,
    children?: any,
    label: string,
    onOK?: () => void,
    onClose?: () => void,
    submitTitle?: string
}

const Modal = (
    {
        children,
        visible = false,
        label,
        onClose,
        onOK,
        submitTitle = "Sumbit"
    }: ModalProp
) => {

    if (visible) {
        document.body.classList.add('active-modal')
    } else {
        document.body.classList.remove('active-modal')
    }

    console.log(visible)
    return (
        <>
