import { memo, useState, useCallback, useEffect } from 'react';
import "./style.css";
import SelectWithApi from "../../components/Select/SelectWithApi";
import Button from "../../components/button/button"


interface itemsType {
    itemCode: number;
    item: string;
    width: number;
    height: number;
    size: number;
    quantity: number;
    price: number;
    total: number;
    notes?: string;
}

const SupplierInvoice = () => {

    const [items, setItems] = useState<itemsType[]>([
        {
            itemCode: 0,
            item: 'item 1',
            width: 0,
            height: 0,
            size: 0,
            quantity: 0,
            price: 0,
            total: 0
        }
    ])
    const [activeItem, setActiveItem] = useState<itemsType>({
        itemCode: 0,
        item: '',
        width: 0,
        height: 0,
        size: 0,
        quantity: 0,
        price: 0,
        total: 0
    })

    const handleAdd = useCallback(() => {

    }, []);
    const handleDelete = useCallback(() => {

    }, [])
    //@ts-ignore
    const handleWidth = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, width: +event.target.value }) }, [activeItem])
    const handleHeight = useCallback((event: { target: { value: string | number; }; }) => {
        setActiveItem({ ...activeItem, height: +event.target.value, size: activeItem.height * activeItem.width })
    }, [activeItem])

    //const handleHeight = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, height: +event.target.value }) }, [activeItem])
    //const handleHeight = useCallback((event: { target: { value: string | number; }; }) => { setActiveItem({ ...activeItem, height: +event.target.value }) }, [activeItem])

    console.log(activeItem)
    return (
        <>
            <div className="SupplierInvoice">
                <div className="Table">
                    <section>
                        <div className="tools">
                            <div className="TableTools">
                                <button >
                                    <i className="fa-sharp fa-solid fa-plus"></i>
                                </button>
                                <button>
                                    <i className="fa-solid fa-floppy-disk"></i>
                                </button>
                                <button  >
                                    <i className="fa-sharp fa-solid fa-broom"></i>
                                </button>
                            </div>
                        </div>
                        <div className="tbl-header">
                            {/* <SelectWithApi
                                Api={"basicData/customer_pop_data"}
                            /> */}
                            <table>
                                <thead>
                                    <tr>
                                        <th className='item'>Item</th>
                                        <th>Width</th>
                                        <th>Heigh</th>
                                        <th>Size</th>
                                        <th>Quantity</th>
                                        <th>Price</th>
                                        <th>Total</th>
                                        <th>Notes</th>
                                        <th></th>
                                    </tr>
                                </thead>
                            </table>
                        </div>
                        <div className="tbl-content">
                            <table>
                                <tbody>
                                    {items.map((item) => {
                                        return (<tr key={item.itemCode}>
                                            <td className='item'>{item.item}</td>
                                            <td>{item.width}</td>
                                            <td>{item.height}</td>
                                            <td>{item.size}</td>
                                            <td>{item.quantity}</td>
                                            <td>{item.price}</td>
                                            <td>{item.total}</td>
                                            <td>{item.notes}</td>
                                            <td><Button
                                                label={"Delete"}
                                                onClick={handleDelete}
                                            /></td>
                                        </tr>)
                                    })}
                                    <tr>
                                        <td className='item'><SelectWithApi
                                            Api={"basicData/items_pop_data"}
                                        /></td>
                                        <td><input type="text" onChange={handleWidth} /></td>
                                        <td><input type="text" onChange={handleHeight} /></td>
                                        <td><input type="text" onChange={handleWidth} disabled value={activeItem.size} /></td>
                                        <td><input type="text" onChange={handleWidth} /></td>
                                        <td><input type="text" onChange={handleWidth} /></td>
                                        <td><input type="text" onChange={handleWidth} disabled /></td>
                                        <td><input type="text" onChange={handleWidth} /></td>
                                        <td><Button
                                            label={"Delete"}
                                            disabled
                                        /></td>
                                    </tr>
                                    <div className="add_Btn">
                                        <Button
                                            label={"add"}
                                            onClick={handleAdd}
                                        />
                                    </div>
                                </tbody>
                            </table>
                        </div>
                    </section>
                </div >
            </div>
        </>
    )
}

export default memo(SupplierInvoice);
