//             p_language: 2,
//             chunk_labels: chunkLabel
//         }
//     })
//      runFetch()
//      setChunkLabels(data)
// },[chunkLabel, data, runFetch])


useEffect(()=>{
    if(label !== ""){
        runFetch()
    }
// eslint-disable-next-line react-hooks/exhaustive-deps
},[label])

return {tran_label: data[label]}

}

export default useTranslateLabel
