import { memo, useCallback, useEffect } from "react";
import SelectWithApi from "../../../components/Select/SelectWithApi";
import InputText from "../../../components/InputText/InputText";
import DatePicker from "../../../components/datePicker/DatePicker";

interface InsertFormProp {
    activeItem: any;
    setActiveItem: any;
    itemState: any;
    itemChange: any;
    state: any;
    onChange: any
}

const InsertForm = ({
    activeItem,
    setActiveItem,
    itemChange,
    itemState,
    state,
    onChange
}: InsertFormProp) => {

    const { item_id, width, height, size, quantity, price, total, notes, itemName } = itemState

    useEffect(() => {
        setActiveItem({ ...activeItem, size: +width * +height, total: +quantity * +price * +width * +height })
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [height, width, quantity, price])


    const handleItemChange = useCallback((onChange: any) => {
        //@ts-ignore
        setActiveItem({ ...activeItem, itemName: onChange.label, [onChange.name]: onChange.value })
    }, [activeItem, setActiveItem])

    return (
        <>
            <div style={{ display: "flex", flexDirection: "row", width: "100%" }}>
                <SelectWithApi
                    Api={"basicData/supplier_pop_data"}
                    onChange={onChange}
                    value={state.supplier_id}
                    Label="Supplier"
                    width="10%"
                />
                <DatePicker
                    value={state.date}
                    setValue={onChange}
                />
            </div>
            <div style={{ display: "flex", flexDirection: "row", width: "100%", justifyContent: "space-between" }}>
                <SelectWithApi
                    Api={"basicData/items_pop_data"}
                    onChange={handleItemChange}
                    value={item_id}
                    Label="Item"
                    name="item_id"
                    width="10%"
                />
                <InputText
                    name="width"
                    value={width}
                    Label="width"
                    onChange={itemChange}

                />
                <InputText
                    name="height"
                    value={height}
                    Label="size"
                    onChange={itemChange}
                />
                <InputText
                    name="size"
                    value={size}
                    Label="size"
                    onChange={itemChange}
                    disabled
                />
                <InputText
                    name="quantity"
                    value={quantity}
                    Label="quantity"
                    onChange={itemChange}
                />
                <InputText
                    name="price"
                    value={price}
                    Label="price"
                    onChange={itemChange}
                />
                <InputText
                    name="total"
                    value={total}
                    Label="total"
                    onChange={itemChange}
                    disabled
                />
                <InputText
                    name="notes"
                    value={notes}
                    Label="notes"
                    onChange={itemChange}
                />
            </div>
        </>
    )
}

export default memo(InsertForm)
