import {useState, useCallback} from "react";

interface useFormManagerProps {
    initialVlaue?: any;
}

const useFormManager = ({initialVlaue}: useFormManagerProps)=>{

    const [state, setState] = useState({})

    const onChange = useCallback((change: any)=>{
        //@ts-ignore
            setState({...state, [change.name]: change.value})
    },[state])

    const initialValue = useCallback((values:any)=>{
        setState(values)
    },[])


    return{state, onChange, initialValue}
}

export default useFormManager
