const useFormManager = ()=>{

    const [state, setState] = useState({})

    const onChange = useCallback((change: any)=>{
        //@ts-ignore
            setState({...state, [change.name]: change.value})
    },[state])

    // const initialValue = useCallback((values:any)=>{
    //     console.log(values)
    //     setState(values)
    // },[])
    var kk

    useEffect(()=>{
        //@ts-ignore
        setState(kk)
    },[])

    console.log(state)


    return{state, onChange, kk }
}

export default useFormManager
