import {useState, useEffect, useCallback} from "react";
import {onChangeType} from "../Types/general"

const useFormManager = ()=>{

    const [state, setState] = useState([])
    const [change, setChangea] = useState<onChangeType>({
        name: "",
        value: ""
    })

    // useEffect(()=>{
    //     setState({...state, [change.name]: change.value})
    // },[change.name, change.value, state])

    const onChange = useCallback((change: any)=>{
        // const obj = {[change.name]: change.value};
            setState({...state, [change.name]: change.value})
    },[])


    return{state, onChange}
}

export default useFormManager
