import { useEffect, useState, useCallback } from "react";
import useFetch from "./useFetch";

interface useTranslateLabelProps{
    label: string;
    chunkLabels?: []
}

interface FetchOptionsType{
    link?: string;
    params:{
        label?: string;
        p_language: 1 | 2,
        chunk_labels?: string[]
    }
}

const useTranslateLabel = ({label}: useTranslateLabelProps)=>{

    const [fetchOptions, setFetchOptions] = useState<FetchOptionsType>({
        link: "QUERY_LABELS",
        params: {
            label: label,
            p_language: 2,
            chunk_labels: []
        }
    })



const { data, runFetch}  = useFetch({
    link: fetchOptions.link,
    params:fetchOptions.params,
    fetchOnFirstRun: false
})

const labels = useCallback((e: any)=>{
    setFetchOptions({
        link: "QUERY_CHUNK_LABELS",
        params:{
            p_language: 2,
            chunk_labels: e.labels
        }
    })
     runFetch()
},[runFetch])

useEffect(()=>{
    if(label){
        runFetch()
    }
// eslint-disable-next-line react-hooks/exhaustive-deps
},[label])

return {tran_label: data[label], chunkLabels: data[0], labels}

}

export default useTranslateLabel
