import { memo } from "react";
interface SelectProps {
    Option?: {
        label: string,
        value: number
    };
    Options: {
        label: string,
        value: number
    }[]
    setValue?: any;
    Label?: boolean;
    value?: number;
}

const Select = ({ Options, setValue, Label = false, value = 0 }: SelectProps) => {
    //@ts-ignore
    const handleValue = (event) => {
        Options.map((option) => {
            if (option.value === +event.target.value) {
                if (Label) {
                    setValue(option)
                } else {
                    setValue(option.value)
                }
            }
        })
    }
    return (
        <>
            <select name="cars" onChange={handleValue} placeholder="">
                <option value="" hidden ></option>
                {Options.map((Option) => {
                    return (
                        <option key={Option.value} value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                    )
                })}
            </select>
        </>
    )
}

export default memo(Select)
