import { memo, useCallback } from "react";
import { radioBoxProps, option } from "./interface"

const RadioBox = ({ name, options, width, onChange }: radioBoxProps) => {
    const handleChange = useCallback((value: string | number) => {
        onChange({ name: name, value: value })
    }, [name, onChange])
    return (
        <>
            {options.map((option: option) => {
                return (
                    <div className="form-check">
                        <input className="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault1" checked={option.checked} onChange={handleChange(option.value)} />
                        <label className="form-check-label" htmlFor="flexRadioDefault1">
                            {option.label}
                        </label>
                    </div>
                )
            })}
        </>
    )
}

export default memo(RadioBox)
