import { useState, useEffect, useCallback } from "react";

interface useFetchProp {
  link: string;
  fetchOnFirstRun?: boolean;
  refreshTimeout?: number;
}

const useFetch = ({link, fetchOnFirstRun, refreshTimeout}: useFetchProp) => {
  const url = `http://144.24.209.19:9090/api/v1/${link}`
    const [data, setData] = useState<any>([]);

const getData = useCallback(async(link: string)=>{
    const response=await fetch(link);
    const apiData=await response.json();  
  setData(apiData);
},[])

    useEffect(() => {
      // if(fetchOnFirstRun){
      getData(url)
      // }
      }, [getData, url, fetchOnFirstRun]);

      const refresh = ()=>{
        if(fetchOnFirstRun){
      getData(url)
      }
      }

      // setInterval(refresh, 60000)

      return{data, refresh, setData}
}

export default useFetch
