import { memo } from "react";
interface SelectProps {
    Option?: {
        label: string,
        value: number
    };
    Options: {
        label: string,
        value: number
    }[]
    onChange?: any;
    Label?: boolean;
    value?: number;
    name?: string
}

const Select = ({ Options, onChange, Label = false, value = 0, name }: SelectProps) => {
    //@ts-ignore
    const handleValue = (event) => {
        Options.map((option) => {
            if (option.value === +event.target.value) {
                if (Label) {
                    onChange({ value: option.value, label: option.label, name: name })
                } else {
                    onChange(option.value)
                }
            }
        })
    }
    return (
        <>
            <div style={{ width: "100%" }}>
                <select name="cars" onChange={handleValue} placeholder="">
                    <option value="" hidden ></option>
                    {Options.map((Option) => {
                        return (
                            <option key={Option.value} value={Option.value} selected={value === Option.value && true}> {Option.label}</option>
                        )
                    })}
                </select>
            </div>
        </>
    )
}

export default memo(Select)
