import { memo } from "react";
import Select from "./Select";
import useFetch from "../../hooks/useFetch";

interface SelectWithApiProps {
    Api: string;
    setValue?: any;
    setLabel?: any;
    value?: number
}

const SelectWithApi = ({ Api, setValue, setLabel, value }: SelectWithApiProps) => {

    const { data } = useFetch(Api)

    return (
        <>
            <Select
                Options={data}
                setValue={setValue}
                setLabel={setLabel}
                value={value}
            />
        </>
    )
}

export default memo(SelectWithApi);
