import { memo } from "react";
import Table from "../../components/TableView/Table";
import SelectWithApi from "../../components/Select/SelectWithApi";
import Flex from "../../components/Flex/Flex";
import useFormManager from "../../hooks/useFormManager";
import { columns } from "./constans";
import useFetch from "../../hooks/useFetch";
import SearchButton from "../../components/button/searchButton";
import InputText from "../../components/InputText/InputText";

const EmployeeSheet = () => {
    const { state, onChange } = useFormManager({ initialValue: { employee_id: "", date_from: "", date_to: "" } })
    const { data, runFetch } = useFetch({
        link: "QUERY_EMPLOYEE_SHEET_SALARY",
        params: {
            employee_id: state.employee_id
        }
    })
    const {
        daysData,
        total_late_time,
        total_over_time,
        total_early_leaving,
        net_salary,
        salary,
        late_cost,
        over_time_cost
    } = data
    return (
        <>
            <Flex flexDirection="column" padding="0" margin="0" width="100%">
                <Flex width="100%" margin="0" padding="0" bordered>
                    <SelectWithApi
                        Api="QUERY_EMPLOYEE_NAME_LIST"
                        name="employee_id"
                        Label="emply"
                        onChange={onChange}
                        fetchOnFirstRun
                    />
                    <InputText
                        value={total_late_time}
                        Label="total_late_time"
                        disabled
                    />
                    <InputText
                        value={total_late_time}
                        Label="total_late_time"
                        disabled
                    />
                    <SearchButton
                        onClick={runFetch}
                    />
                </Flex>
                <Table
                    dataSource={daysData}
                    columns={columns}
                    hideTools={false}
                    canPrint={true}
                // onPrint
                />
                <Flex width="100%" margin="0" padding="0" justifyContent="space-around">
                    <InputText
                        value={total_late_time}
                        Label="total_late_time"
                        disabled
                    />
                    <InputText
                        value={total_over_time}
                        Label="total_over_time"
                        disabled
                    />
                    <InputText
                        value={total_early_leaving}
                        Label="total_early_leaving"
                        disabled
                    />
                    <InputText
                        value={net_salary}
                        Label="net_salary"
                        disabled
                    />
                    <InputText
                        value={salary}
                        Label="salary"
                        disabled
                    />
                    <InputText
                        value={late_cost}
                        Label="late_cost"
                        disabled
                    />
                    <InputText
                        value={over_time_cost}
                        Label="over_time_cost"
                        disabled
                    />
                </Flex>
            </Flex>
        </>
    )
}

export default memo(EmployeeSheet)
