import { memo, useCallback } from "react";
import Select from "./Select";
import useFetch from "../../hooks/useFetch";

interface SelectWithApiProps {
    Api: string;
    setValue?: any;
    setLabel?: any;
    value?: number
}

const SelectWithApi = ({ Api, setValue, setLabel, value = 0 }: SelectWithApiProps) => {

    const { data } = useFetch(Api)
    if (setLabel) {
        useCallback(() => {
            data.map((option: any) => {
                if (option.value === +value) {
                    setLabel(option.label)
                }
            })
        }, [data, setLabel, value])
    }

    return (
        <>
            <Select
                Options={data}
                setValue={setValue}
                setLabel={setLabel}
                value={value}
            />
        </>
    )
}

export default memo(SelectWithApi);
